 DROP database gameofthrones;
CREATE DATABASE IF NOT EXISTS gameofthrones;
USE gameofthrones;

CREATE TABLE Usuario (
idUsuario INT PRIMARY KEY AUTO_INCREMENT,
nome VARCHAR(45) NOT NULL,
email VARCHAR(256),
senha VARCHAR(100),
CONSTRAINT ctChkEmail CHECK (email LIKE "%@%"),
imgPerfil VARCHAR(2048) DEFAULT "https://github.com/Fernandeess/imagePerfil/blob/main/user.png?raw=true",
bio VARCHAR(200),
fkCasa int,
FOREIGN KEY (fkCasa) REFERENCES Casa(idCasa)
) ;
-- ALTER TABLE Usuario MODIFY COLUMN bio VARCHAR(500);
CREATE TABLE Casa(
idCasa INT PRIMARY KEY AUTO_INCREMENT,
nomeCasa VARCHAR(45),
brasaoCasa varchar(2048),
lema VARCHAR(256)
) AUTO_INCREMENT = 1000;

CREATE TABLE Quiz(
idQuiz INT PRIMARY KEY AUTO_INCREMENT,
nomeQuiz VARCHAR(45)
);
DROP TABLE Pontos;
CREATE TABLE Pontos(
idPontos int auto_increment, 
fkUsuario int,
fkQuiz int,
FOREIGN KEY (fkUsuario) REFERENCES Usuario(idUsuario),
FOREIGN KEY (fkQuiz) REFERENCES Quiz(idQuiz),
pontos int default 0,
PRIMARY KEY(idPontos,fkUsuario,fkQuiz)
);

